import React, { useState } from "react"
import styles from '../styles/styles.css'
const TextInput = props => {
    const [textInputValue, setTextInputValue] = useState('')
    const setTextInputValueHandler = event => {
        setTextInputValue(event.target.value)
    }
    const handleAdd = () => {
        props.handleAdd(textInputValue)
        setTextInputValue('');
    }
    return(
        <div className = "scoreboard-headline-wrapper">
            <input type="text" value={textInputValue} placeholder={props.text} onChange = {setTextInputValueHandler}></input>
            <button onClick = {handleAdd}>>></button>
        </div>
    )
}
export default TextInput
